'use strict'

import axios from 'axios'

export default class Base {
  constructor (realm, key, secret) {
    this.realm = realm
    this.key = key
    this.secret = secret
  }

  formatUrl (path) {
    return `https://${this.realm}.checkbook.io/v3${path}`
  }

  request (method, path, params = {}) {
    let headers = {
      'Authorization': `${this.key}:${this.secret}`,
      'Content-Type': 'application/json'
    }
    return axios({
      method: method,
      url: this.formatUrl(path),
      data: params,
      headers: headers
    })
  }
}

import Base from './abstract/base'

export default class Bank extends Base {
  /**
   * Create a new bank account
   * @param  {string} routing routing number
   * @param  {string} account account number
   * @param  {string} type    type of bank account (checking or business)
   * @return {promise}        Response from network
   */
  create (routing, account, type) {
    return this.request('POST', '/bank', {
      routing,
      account,
      type
    })
  }

  /**
   * Get all bank accounts
   * @return {promise} Response from the network
   */
  getAll () {
    return this.request('GET', '/bank')
  }

  /**
   * Remove a bank account by identifier
   * @param  {string} id Previously aquired bank id
   * @return {promise}   Response from the network
   */
  remove (id) {
    return this.request('DELETE', `/bank/${id}`)
  }

  /**
   * Create a new bank account
   * @param  {string} id      id returned from the institutions endpoint
   * @param  {string} account account number
   * @param  {string} type    type of bank account (checking or business)
   * @return {promise}        Response from network
   */
  createIAV (id, account, type) {
    return this.request('POST', '/bank/iav', {
      institution_id: id,
      account,
      type
    })
  }

  /**
   * Get all institutions
   * @return {promise} Response from the network
   */
  getInstitutions () {
    return this.request('GET', '/bank/institutions')
  }

  /**
   * Verify a bank account
   * @param  {number} amount_1 First micro deposit amount
   * @param  {number} amount_2 Second micro deposit amount
   * @return {promise}         Response from the network
   */
  verify (amount1, amount2) {
    return this.request('POST', '/bank/verify', {
      amount_1: amount1,
      amount_2: amount2
    })
  }
}

import Base from './abstract/base'

export default class Check extends Base {
  /**
   * Remove a check by identifier
   * @param  {string} id Previously aquired identifier of a check
   * @return {promise}   Response from the network
   */
  remove (id) {
    return this.request('DELETE', `/check/${id}`)
  }

  /**
   * Get all checks for the user
   * @return {promise} Reponse from the network
   */
  getAll () {
    return this.request('GET', '/check')
  }

  /**
   * Get information about a check by identifier
   * @param  {string} id Previously aquired identifier of check
   * @return {promise}   Response from the network
   */
  get (id) {
    return this.request('GET', `/check/${id}`)
  }

  /**
   * Create a new digital check
   * @param  {String} name      Name of the recipient
   * @param  {String} email     Email of the recipient
   * @param  {number} amount    Amount to send the recipient
   * @return {promise}          Response from the network
   */
  createDigital (name, email, amount) {
    return this.request('POST', '/check/digital', {
      name,
      recipient: email,
      amount: Number(amount)
    })
  }

  /**
   * Create a new physical check
   * @param  {String} name    Name of the recipient
   * @param  {object} address address of the recipient ({"line_1": "1234 N. 1st Street","line_2": "#56","city": "San Francisco","state": "CA","zip": "12345"})
   * @param  {number} amount  Amount to send the recipient
   * @return {promise}         Response from the network
   */
  createPhysical (name, address, amount) {
    return this.request('POST', '/check/physical', {
      name,
      recipient: address,
      amount: Number(amount)
    })
  }
}

import Base from './abstract/base'

export default class Invoice extends Base {
  /**
   * Get all invoices
   * @return {promise} Response from the network
   */
  getAll () {
    return this.request('GET', '/invoice')
  }

  /**
   * Create a new invoices
   * @param  {string} recipient   Email or id of recipient
   * @param  {string} name        Name of recipient
   * @param  {float} amount       Amount for invoicing
   * @param  {string} description Description of the invoice
   * @param  {object} options     Aditional options for the request that are considered optional (account, attachment)
   * @return {promise}            Response from the network
   */
  create (recipient, name, amount, description, options = {}) {
    let body = {
      recipient,
      name,
      amount,
      description
    }
    Object.keys(options).forEach((key) => {
      body[key] = options[key]
    })
    return this.request('POST', '/invoice', body)
  }

  /**
   * Remove an invoice
   * @param  {string} id Previously retrieved identifier
   * @return {promise}   Response from the network
   */
  remove (id) {
    return this.request('DELETE', `/invoice/${id}`)
  }

  /**
   * Get information about a specific invoice
   * @param  {string} id Previously aquired identifier of an invoice
   * @return {promise}   Response from the network
   */
  get (id) {
    return this.request('GET', `/invoice/${id}`)
  }
}

import Base from './abstract/base'

export default class Subscription extends Base {
  /**
   * Get all subscriptions for the user
   * @return {promise} Response from the network
   */
  getAll () {
    return this.request('GET', '/subscription')
  }

  /**
   * Remove a subscription
   * @param  {string} id Previously aquired identifier of subscription
   * @return {promise}   Response from the network
   */
  remove (id) {
    return this.request('DELETE', `/subscription/${id}`)
  }

  /**
   * Get information about specific subscription
   * @param  {string} id Previously aquired identifier of subscription
   * @return {promise}   Response from the network
   */
  get (id) {
    return this.request('GET', `/subscription/${id}`)
  }

  /**
   * Update an existing subscription
   * @param  {string} id      Previously aquired identifier of subscription
   * @param  {object} options Values to be updated
   * @return {promise}        Response from the network
   */
  update (id, options) {
    return this.request('PUT', `/subscription/${id}`, options)
  }

  /**
   * Create a new check subscription
   * @param  {string} recipient email/id of recipient
   * @param  {string} name      Name of recipient
   * @param  {float} amount     Amount for the charge
   * @param  {string} inverval  How often the subscription will recur: WEEKLY, MONTHLY
   * @param  {object} options   Optional options for the request (start_date, description, duration, account)
   * @return {promise}          Response from the network
   */
  createCheck (recipient, name, amount, interval, options = {}) {
    let body = {
      name,
      recipient,
      amount: Number(amount),
      interval
    }
    Object.keys(options).forEach((key) => {
      body[key] = options[key]
    })

    return this.request('POST', '/subscription/check', body)
  }

  /**
   * Create a new invoice subscription
   * @param  {string} recipient email/id of recipient
   * @param  {string} name      Name of recipient
   * @param  {float} amount     Amount for the charge
   * @param  {string} description Description of the charge
   * @param  {string} inverval  How often the subscription will recur: WEEKLY, MONTHLY
   * @param  {object} options   Optional options for the request (start_date, description, duration, account)
   * @return {promise}          Response from the network
   */
  createInvoice (recipient, name, amount, description, interval, options = {}) {
    let body = {
      name,
      recipient,
      amount: Number(amount),
      description,
      interval
    }
    Object.keys(options).forEach((key) => {
      body[key] = options[key]
    })

    return this.request('POST', '/subscription/invoice', body)
  }
}

import Base from './abstract/base'

